---TẠO DATABASE VÀ BẢNG--- create database HMN_QLDSV use HMN_QLDSV CREATE TABLE KETQUA ( 	MASV CHAR(3), 	MAMH CHAR(2), 	LANTHI TINYINT, 	DIEM DECIMAL(4,2) 	CONSTRAINT PK_KETQUA PRIMARY KEY(MASV,MAMH,LANTHI), 	CONSTRAINT FK_MASV FOREIGN KEY(MASV) 		REFERENCES DMSV(MASV), 	CONSTRAINT FK_MAMH FOREIGN KEY(MAMH) 		REFERENCES DMMH(MAMH) ); select * from KETQUA create table DMKHOA ( 	MAKHOA CHAR(2), 	TENKHOA NVARCHAR(30), 	CONSTRAINT PK_DMKHOA PRIMARY KEY(MAKHOA) ); SELECT * FROM DMKHOA CREATE TABLE DMMH ( 	MAMH CHAR(2), 	TENMH NVARCHAR(35), 	SOTIET TINYINT, 	CONSTRAINT PK_DMMH PRIMARY KEY(MAMH) ) SELECT * FROM DMMH CREATE TABLE DMSV ( 	MASV CHAR(3), 	HOSV NVARCHAR(30), 	TENSV NVARCHAR(10), 	PHAI NCHAR(10), 	NGAYSINH DATE, 	NOISINH NVARCHAR(25), 	MAKH CHAR(2), 	HOCBONG FLOAT, 	CONSTRAINT PK_DMSV PRIMARY KEY (MASV) ) SELECT*FROM DMSV ---Nhập dữ liệu--- ----Nhập vào bảng DMMH---- INSERT INTO DMMH (MAMH,TENMH,SOTIET) VALUES	(01, N'Cơ sở dữ liệu',45), 		(02,N'Trí tuệ nhân tạo',45), 		(03, N'Truyền tin',45), 		(04, N'Đồ họa', 60), 		(05, N'Văn phạm', 60), 		(06, N'Kỹ thuật lập trình', 45); select * from DMMH ----Nhập vào bảng DMKHOA---- insert into DMKHOA (MAKHOA,TENKHOA) values	('AV', N'Anh Văn'), 		('TH', N'Tin học'), 		('TR', N'Triết'), 		('VL', N'Vật Lý');  ----Nhập vào bảng DMSV---- insert into DMSV (MASV,HOSV,TENSV,PHAI,NGAYSINH,NOISINH,MAKH,HOCBONG) values  ('A01',N'Nguyễn thị',N'Hải',N'Nữ',CONVERT(DATE,'23/02/1993',103),N'Hà Nội','TH',130000), 		('A02',N'Trần văn',N'Chính',N'Nam',CONVERT(DATE,'24/12/1992',103),N'Bình Định','VL',150000), 		('A03',N'Lê thu bạch',N'Yến',N'Nữ',CONVERT(DATE,'21/02/1993',103),N'Tp HCM','TH',170000), 		('A04',N'Trần anh',N'Tuấn',N'Nam',CONVERT(DATE,'20/12/1994',103),N'Hà Nội','AV',80000), 		('B01',N'Trần thanh',N'Mai',N'Nữ',CONVERT(DATE,'12/08/1993',103),N'Hải Phòng','TR',0), 		('B02',N'Trần thị thu',N'Thủy',N'Nữ',CONVERT(DATE,'02/01/1994',103),N'Tp HCM','AV',0); select * from DMSV ----Nhập vào bảng KETQUA---- insert into KETQUA (MASV,MAMH,LANTHI,DIEM) values	('A01','1',1,3), 		('A01','1',2,6), 		('A01','2',2,6), 		('A01','3',1,5), 		('A02','1',1,4.5), 		('A02','1',2,7), 		('A02','3',1,10), 		('A02','5',1,9), 		('A03','1',1,2), 		('A03','1',2,5), 		('A03','3',1,2.5), 		('A03','3',2,4), 		('A04','5',2,10), 		('B01','1',1,7), 		('B01','3',1,2.5), 		('B01','3',2,5), 		('B02','2',1,6), 		('B02','4',1,10); SELECT * FROM KETQUA ---BAI 3.1: Thêm thuộc tính NamTL vào DMKHOA--- alter table DMKHOA  add NamTL int select * from DMKHOA ---BÀI 3.2: Thay đổi kiểu dữ liệu NamTL thành smallint--- alter table DMKHOA alter column NamTL smallint ---BÀI 3.3: Đổi tên thuộc tính NamTL thành NamThanhLap--- exec sp_rename 'DMKHOA.NamTL','NamThanhLap' ---BÀI 3.4: Xóa thuộc tính NamThanhLap--- alter table DMKHOA drop column NamThanhLap ---BÀI 3.6: Tạo ràng buộc khóa ngoại giữa sinh viên và khoa--- alter table DMSV add constraint fk_dmsv_khoa  foreign key (MAKH) 	REFERENCES DMKHOA (MAKHOA) ---BÀI 3.5: Xóa ràng buộc khóa ngoại giữa sinh viên và khoa--- alter table dmsv drop fk_dmsv_khoa ---BÀI 4.1:  Thêm tất cả dữ liệu bằng lệnh insert --- /* đã làm ở trên */ ---BÀI 4.2:  Cập nhật số tiết của môn văn phạm thành 45 tiết--- update dmmh set SOTIET =45 where TENMH=N'Văn Phạm' select*from dmmh ---Bài 4.3: Cập nhật tên của sinh viên Trần Thanh Mai thành Trần Thanh Kỳ--- update DMSV set TENSV=N'Kỳ' where TENSV=N'Mai' and HOSV=N'Trần Thanh' ---Bài 4.4: Cập nhật phái của sinh viên Trần Thanh Kỳ thành phái Nam--- update DMSV set PHAI='Nam' where TENSV=N'Kỳ' and HOSV=N'Trần Thanh' ---BÀI 4.5: Cập nhật ngày sinh của sinh viên Trần Thị Thu Thùy thành 05/07/1990--- update DMSV set NGAYSINH = convert(date,'05/07/1990',103) where MASV='B02' ---BÀI 4.6: Tăng học bổng cho tất cả sinh viên của khoa anh văn thêm 100,000đ--- update DMSV set HOCBONG = HOCBONG +100000 where MAKH ='AV' select*from dmsv ----BÀI 4.7:  Xóa tất cả những dòng có điểm thi lần 2 nhỏ hơn 5 trong bản kết quả--- delete from KETQUA where LANTHI=2 and DIEM<5 select*from KETQUA ---BÀI 4.8: Xóa tất cả những sinh viên không có học bổng--- /*Không xóa được vì sinh viên có liên kết ràng buộc khóa ngoại với khoa thông qua tham chiếu mã khoa */